// Get current game time
  const gameTime = variables['d3e810fc-452e-4b4c-8553-1eb9c6910d77'];

  // If game time is not initialized, initialize it
  if (!gameTime || !gameTime.realTimeStarted) {
    variables['d3e810fc-452e-4b4c-8553-1eb9c6910d77'] = {
      days: 0,
      hours: 0,
      minutes: 0,
      realTimeStarted: Date.now()
    };
    return;
  }

  // Calculate elapsed real time in milliseconds
  const realTimeElapsed = Date.now() - gameTime.realTimeStarted;

  // Convert to game time (3 real hours = 1 game day)
  // 1 real minute = 8 game minutes
  const totalGameMinutes = Math.floor(realTimeElapsed / (1000 * 60) * 8);

  // Calculate days, hours, minutes
  const gameDays = Math.floor(totalGameMinutes / (24 * 60));
  const gameHours = Math.floor((totalGameMinutes % (24 * 60)) / 60);
  const gameMinutes = totalGameMinutes % 60;

  // Update game time
  variables['d3e810fc-452e-4b4c-8553-1eb9c6910d77'] = {
    days: gameDays,
    hours: gameHours,
    minutes: gameMinutes,
    realTimeStarted: gameTime.realTimeStarted
  };

