      id: Date.now(),
      type: 'error',
      message: 'Ошибка: не указан ангар или новое имя'
    });
    return;
  }

  // Validate name length
  if (context.parameters['newName'].length < 2 || context.parameters['newName'].length > 20) {
    variables['84cd6db0-07ad-4f6f-9b46-b347c76f03b7'].push({
      id: Date.now(),
      type: 'error',
      message: 'Имя должно содержать от 2 до 20 символов'
    });
    return;
  }

  // Get hangars
  const hangars = variables['bf98c673-5dad-430b-9db3-55cc24925b57'];
  if (!hangars) {
    variables['84cd6db0-07ad-4f6f-9b46-b347c76f03b7'].push({
      id: Date.now(),
      type: 'error',
      message: 'Ошибка: список ангаров не найден'
    });
    return;
  }

  // Find hangar by index
  const hangarIndex = parseInt(context.parameters['hangarId']);
  if (isNaN(hangarIndex) || hangarIndex < 0 || hangarIndex >= hangars.length) {
    variables['84cd6db0-07ad-4f6f-9b46-b347c76f03b7'].push({
      id: Date.now(),
      type: 'error',
      message: 'Ошибка: ангар не найден'
    });
    return;
  }

  // Store old name for notification
  const oldName = hangars[hangarIndex].name;

  // Update hangar name
  hangars[hangarIndex].name = context.parameters['newName'];

  // Update hangars
  variables['bf98c673-5dad-430b-9db3-55cc24925b57'] = [...hangars];

  // Show notification
  variables['84cd6db0-07ad-4f6f-9b46-b347c76f03b7'].push({
    id: Date.now(),
    type: 'success',
    message: `Ангар переименован: ${oldName} → ${context.parameters['newName']}`
  });

  return {
    success: true,
    hangarId: context.parameters['hangarId'],
    oldName: oldName,
    newName: context.parameters['newName']
  };
